package loki_test

import (
	"context"
	"fmt"
	"io"
	"net/http"
	"net/http/httptest"
	"strconv"
	"testing"
	"time"

	"github.com/golang/snappy"
	"github.com/stretchr/testify/require"
	"google.golang.org/protobuf/proto"

	"github.com/obolnetwork/charon/app/log/loki"
	pbv1 "github.com/obolnetwork/charon/app/log/loki/lokipb/v1"
)

func TestLoki(t *testing.T) {
	const (
		serviceLabel    = "test"
		otherLabelKey   = "k"
		otherLabelValue = "v"
	)

	received := make(chan string)
	srv := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		b, err := io.ReadAll(r.Body)
		require.NoError(t, err)
		require.NoError(t, r.Body.Close())
		req := decode(t, b)
		require.Len(t, req.GetStreams(), 1)
		require.Contains(t, req.GetStreams()[0].GetLabels(), fmt.Sprintf(`service="%s"`, serviceLabel))
		require.Contains(t, req.GetStreams()[0].GetLabels(), fmt.Sprintf(`%s="%s"`, otherLabelKey, otherLabelValue))
		for _, entry := range req.GetStreams()[0].GetEntries() {
			received <- entry.GetLine()
		}
	}))

	// Only return lazy labels after 3 attempts.
	var count int
	lazyLabels := func() (map[string]string, bool) {
		count++
		if count < 3 {
			return nil, false
		}

		return map[string]string{
			otherLabelKey: otherLabelValue,
		}, true
	}

	cl := loki.NewForT(srv.URL, serviceLabel, time.Millisecond, 1024, 0, lazyLabels, func(_ string, _ error) {})

	const n = 4

	go cl.Run()

	for i := range n {
		cl.Add(strconv.Itoa(i))
	}

	for i := range n {
		require.Equal(t, strconv.Itoa(i), <-received)
	}

	cl.Stop(context.Background())
}

func decode(t *testing.T, b []byte) *pbv1.PushRequest {
	t.Helper()

	pb, err := snappy.Decode(nil, b)
	require.NoError(t, err)

	resp := new(pbv1.PushRequest)
	require.NoError(t, proto.Unmarshal(pb, resp))

	return resp
}

func TestLongLines(t *testing.T) {
	const (
		// copied fromm client.go, simulate production workload
		batchMax      = 5 * 1 << 20
		maxLogLineLen = 4 << 10

		serviceLabel = "test"
		normal       = "normal log line"
		huge         = `ts=2023-07-26T11:38:47.922888118+02:00 level=debug caller=loki/client_test.go:124 msg=error  pretty="DEBG            error                                    {\"data\": \"\"}"`
	)

	var (
		ctx         = context.Background()
		entriesChan = make(chan string)
	)

	srv := httptest.NewServer(http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		b, err := io.ReadAll(r.Body)
		require.NoError(t, err)
		require.NoError(t, r.Body.Close())
		req := decode(t, b)
		require.Len(t, req.GetStreams(), 1)
		require.Contains(t, req.GetStreams()[0].GetLabels(), fmt.Sprintf(`service="%s"`, serviceLabel))
		for _, entry := range req.GetStreams()[0].GetEntries() {
			go func(entry string) {
				entriesChan <- entry
				close(entriesChan)
			}(entry.GetLine())
		}
	}))

	cl := loki.NewForT(srv.URL, serviceLabel, time.Millisecond, batchMax, maxLogLineLen, func() (map[string]string, bool) {
		return map[string]string{}, true
	}, func(s string, err error) {
		// err will never be nil, use require package to pretty print it
		require.NoError(t, err, "reason: %s", s)
	})

	go cl.Run()

	cl.Add(normal)

	for range 1000 {
		cl.Add(huge)
	}

	cl.Stop(ctx)

	var entries []string
	for entry := range entriesChan {
		require.NotEqual(t, huge, entry)
		entries = append(entries, entry)
	}

	require.Len(t, entries, 1)
	require.Equal(t, normal, entries[0])
}
